sys_variable_value.value:
(function execute(inputs, outputs) {

      gs.info('troubleshoot');

      var ratevalue = 5;

      if (inputs.inloantype == 'Housing'){
            var rate = 5;
      }else {rate = 6;}

      var fv = 0;
      var type = 0;
      var rate = Number((ratevalue/100)/12);
      var nper = Number(inputs.incalc_active_month);
      var dsr = Number(inputs.indsr/100);
      var salary = Number(inputs.insalary);
      var pmt = Number(dsr*salary);
  	  //var finalpv = 0;

		//gs.info(pmt);  
		//gs.info(dsr);  
		//gs.info(rate);  


      	  function trunc (x, posiciones = 0) {
            var s = x.toString()
            var l = s.length
            var decimalLength = s.indexOf('.') + 1
            var numStr = s.substr(0, decimalLength + posiciones)
            return Number(numStr)
          }

          function conv_number(expr, decplaces) {
            var str = "" + Math.round(expr * Math.pow(10, decplaces));
            while (str.length <= decplaces) {
              str = "0" + str;
            }
            var decpoint = str.length - decplaces;
            return (str.substring(0, decpoint) + "." + str.substring(decpoint, str.length));
          }

          function pv(rate, nper, pmt, fv,type) {
            if (rate == 0){ 
              pv_value = -(fv + (pmt * nper));
            } else {
              x = Math.pow(1 + rate, -nper);
              y = Math.pow(1 + rate, nper);
              pv_value = -(x * (fv * rate - pmt + y * pmt)) / rate;
            }
            pv_value = conv_number(pv_value, 2);
            return (trunc(pv_value*-1, 2));
          }   

           outputs.pvtot = Number(pv(rate, nper, pmt, fv, type));

          //gs.info('numtotalpv');
          //gs.info(outputs.pvtot);



})(inputs, outputs);

